import React from 'react';
import { Card, Radio, Typography, Row, Col, Empty, Image } from 'antd';
import { FileImageOutlined, FilePdfOutlined } from '@ant-design/icons';

const { Title, Text } = Typography;

export default function ImageSelection({ fileList, setFileList }) {
    // Find the currently selected display image
    const displayImage = fileList.find(file => file.isDisplay);
    
    // Handle display image selection
    const handleDisplaySelection = (uid) => {
        const updatedFiles = fileList.map(file => ({
            ...file,
            isDisplay: file.uid === uid
        }));
        
        setFileList(updatedFiles);
    };
    
    // Check if file is an image
    const isImage = (file) => {
        const imageTypes = ['image/jpeg', 'image/png', 'image/jpg'];
        return file.type && imageTypes.includes(file.type);
    };
    
    // Get file preview URL
    const getPreviewUrl = (file) => {
        if (file.url) {
            return file.url;
        } else if (file.originFileObj) {
            return URL.createObjectURL(file.originFileObj);
        }
        return null;
    };
    
    // Render file preview
    const renderFilePreview = (file) => {
        const previewUrl = getPreviewUrl(file);
        
        if (isImage(file) && previewUrl) {
            return (
                <div className="file-preview-image">
                    <Image 
                        src={previewUrl} 
                        alt={file.name} 
                        style={{ maxHeight: '120px', objectFit: 'contain' }} 
                        fallback="data:image/png;base64,"
                    />
                </div>
            );
        } else {
            return (
                <div className="file-preview-icon">
                    {file.type && file.type.includes('pdf') ? (
                        <FilePdfOutlined style={{ fontSize: '48px', color: '#ff4d4f' }} />
                    ) : (
                        <FileImageOutlined style={{ fontSize: '48px', color: '#1890ff' }} />
                    )}
                    <div className="file-name">{file.name}</div>
                </div>
            );
        }
    };

    return (
        <Card title="ជ្រើសរើសរូបភាពបង្ហាញ" className="mb-6">
            {fileList.length > 0 ? (
                <div className="image-selection-container">
                    <Title level={5} className="mb-4">សូមជ្រើសរើសរូបភាពមួយសម្រាប់បង្ហាញជាចម្បង</Title>
                    
                    <Radio.Group 
                        value={displayImage ? displayImage.uid : null}
                        onChange={(e) => handleDisplaySelection(e.target.value)}
                    >
                        <Row gutter={[16, 16]}>
                            {fileList.map(file => (
                                <Col xs={24} sm={12} md={8} lg={6} key={file.uid}>
                                    <div className={`file-card ${file.isDisplay ? 'selected' : ''}`}>
                                        <Radio value={file.uid}>
                                            {renderFilePreview(file)}
                                        </Radio>
                                    </div>
                                </Col>
                            ))}
                        </Row>
                    </Radio.Group>
                </div>
            ) : (
                <Empty 
                    description="គ្មានឯកសារត្រូវបានបញ្ចូល" 
                    image={Empty.PRESENTED_IMAGE_SIMPLE} 
                />
            )}
        </Card>
    );
}
